  cursor: pointer;
  text-indent: 100%;
  width: 24px;
  height: 24px;
  overflow: hidden;
}

.election-map .btn-close.btn-zoom {
  opacity: .3;
  top: 16px;
  right: 16px;
  position: absolute;
}

.election-map.zoomed .btn-zoom {
  opacity: .95;
  cursor: pointer;
}

.election-map .map {
  width: 100%;
  height: auto;
  stroke-width: .25;
}

.election-map .circonscription {
  fill: #ccc;
  stroke: #000;
  cursor: pointer;
  stroke-linejoin: round;
  stroke-linecap: round;
}

.election-map .department-stroke {
  fill: rgba(255,255,255,0);
  cursor: pointer;
  stroke: #fff;
  stroke-width: .8px;
  stroke-linejoin: round;
  stroke-linecap: round;
  transition: stroke-width .75s ease;
}

.election-map.zoomed .department-stroke {
  stroke-width: .2px;
  pointer-events: none;
}

.election-map .city {
  fill: none;
  cursor: pointer;
  stroke: #fff;
  stroke-width: 0.04;
  stroke-linecap: round;
  stroke-linejoin: round;
}

.election-map .background {
  fill: none;
}

.election-map .tooltip {
  display: none;
  background: white;
