	.container-resultats {
		padding:0 10px;
	}
	.container-resultats > .maj {
		font-family: Helvetica;
		font-size: 15px;
		font-style: italic;
		color: #9B9B9B;
		letter-spacing: 0;
		line-height: 21px;

	}

	.duo-separator {
		/*border-top: 1px dotted #342C68;
		margin-bottom: 30px;
		background: transparent!important;
		letter-spacing: 3px;*/
		background-image: linear-gradient(to right, #342C68 20%, rgba(255, 255, 255, 0) 0%);
		background-position: top;
		background-size: 3px 1px;
		background-repeat: repeat-x;
	}

	.candidat-container {
	    width: 100%;
	    display: flex;
	    -webkit-display: flex;
	    -moz-display: flex;
	    -ms-display: flex;
	    align-items: stretch;
	    -webkit-align-items: stretch;
	    -moz-align-items: stretch;
	    -ms-align-items: stretch;
	    margin-bottom: 20px;
	}

	.container-img {
	    display: inline-block;
	    display: flex;
	    -webkit-display: flex;
	    -moz-display: flex;
	    -ms-display: flex;
	    align-items: center;
	    -webkit-align-items: center;
	    -moz-align-items: center;
	    -ms-align-items: center;
	}

	.img-candidat {
	    width: 56.4px;
	    border: 0;
	}

	.resultats {
	    display: inline-block;
	    margin-left: 7.6px;
	    position: relative;
	    flex: 1 auto;
	    -webkit-flex: 1 auto;
	    -moz-flex: 1 auto;
	    -ms-flex: 1 auto;
	}

	.jauge-container, .parti-candidat, .nom-parti {
		margin-left: 13.6px;
	}

	.candidat-container > .resultats > .nom-parti > span{
		display: none;
	}
	.candidat-container > .resultats > .nom-parti, .candidat-container > .resultats > .nom-parti {
	    font-family: Helvetica;
	    font-weight: bold;
	    font-size: 15px;
	    color: #221A5B ;
	    letter-spacing: 0;
	    margin-bottom: 4px;
	    display: inline;

	}

	.parti-candidat {
	    opacity: 0.5;
	    font-family: Helvetica;
	    font-size: 11px;
	    color: #000000 ;
	    letter-spacing: 1px;
	    margin-bottom: 7.8px;
	}

	.candidat-container > .resultats > .score-container > .score-candidat > span {
		display: none;
	}

	.candidat-container > .resultats > .score-container > .score-candidat-voix > span {
		display: none;
	}
	.score-container {
	    float: right;
	    text-align: right;

	}
	.score-candidat {
		font-size: 28px!important;
		color: #221A5B;
		letter-spacing: 0;
		right: 0;
	}
	.score-candidat-voix {
		font-size: 15px!important;
		color: #221A5B;
		letter-spacing: 0;
		right: 0;
		margin-bottom: 5px;
		font-family: Helvetica;
		font-weight: bold;
	}



	.score-parti-sieges {
	    font-size: 15px!important;
	    color: #221A5B;
	    letter-spacing: 0;
	    right: 0;
	    margin-bottom: 11px;
	    font-family: Helvetica;
	    font-weight: bold;
	    display: inline-block;
	    height: 100%;
	    vertical-align: middle;
	}

	.score-candidat-nb-voix {
		height: 13px;
		opacity: 0.59;
		font-family: Helvetica;
		font-size: 11px;
		color: #000000;
		letter-spacing: 1px;
		margin-top: -7px;
	}

	.score-candidat-nb-sieges {
		/*height: 16px;
		display: inline-block;
		font-family: Helvetica;
		font-size: 11px;
		color: #000000;
		letter-spacing: 1px;*/

		font-size: 28px!important;
	    color: #221A5B;
	    letter-spacing: 0;
	    right: 0;
	    display: inline-block;

	}

	.jauge, .jauge-container {
		height: 5px;
	}

	.jauge-container {
	    width: 100%;
	    background: rgba(52,44,104,0.10);
	}

	.jauge {
		position: absolute;
		background: #342C68;
	}

	.resultats > .nom-parti > a {
	  font-family: Helvetica;
	    font-weight: bold;
	    font-size: 15px;
	    color: #221A5B ;
	    letter-spacing: 0;
	    margin: 0 0 4px 0!important;
	    display: inline;
		text-decoration: none; /* no underline */
		border: none!important
	}

	.container-resultats > .titre-snippet > h2 {
		font-family: Helvetica;
		font-weight: bold;
		color: #221A5B ;
		font-size: 18px;
		margin-left: 0px;
	}
	.container-resultats > .titre-snippet > h6 {
		font-family: Helvetica;
		color: #221A5B ;
		font-size: 12px;
	}
	.container-resultats > .titre-snippet > .sep-titre{
		width:100%;
		border: 2px solid #E8E8EE;
	}

	.source{
		font-family: Helvetica;
		font-size: 13px;
		font-style: italic;
		color: #9B9B9B;
		letter-spacing: 0;
		line-height: 15px;
		text-align:right;
		width:100%;
	}
	.nbdeputes{
		display:none;
	}

	@media (max-width:520px){
	    .co-article .score-candidat {
	        font-size: 16px!important;
	        font-family: Helvetica;
	        font-weight: bold;
	    }

	    .container-img {
	    	width:20%;
	    }
	    .candidat-container > .resultats > .nom-parti, .candidat-container > .resultats > .nom-parti {
	    	font-size:14px;
	    	line-height: 14px;


	    }
	    .score-container {
	    	margin-top:0!important;
	    }
	    .container-img{
	    	display:none;
	    }
	    .candidat-container > .resultats{
	    	padding-left: 7.6px;
	    	margin-left: 0;
	    }

	    .jauge-container, .parti-candidat, .nom-parti {
			margin-left: 0;
		}

		.score-candidat{
			font-family: Helvetica!important;
			font-size: 15px!important;
			color: #221A5B!important;
		}

	}






