}

.election-map.zoomed .department-stroke {
  stroke-width: .2px;
  pointer-events: none;
}

.election-map .city {
  fill: none;
  cursor: pointer;
  stroke: #fff;
  stroke-width: 0.04;
  stroke-linecap: round;
  stroke-linejoin: round;
}

.election-map .background {
  fill: none;
}

.election-map .tooltip {
  display: none;
  background: white;
  box-shadow: 1px 1px 1px 2px #b9c0c5;
  font-size: 12px;
  padding: 10px 12px 8px;
  z-index: 2147483647;
}

.election-map .tooltip.activated {
  display: block;
}

