  display: flex;
  align-items: flex-start;
  padding: 6px 0 0;
  font-weight: bold;
}

.election-map .tooltip .results li:before {
  display: none;
}

.election-map .tooltip .results li:not(:last-child) {
  border-bottom: 1px solid #eef1f5;
  padding-bottom: 6px;
}

.election-map .tooltip .results .name {
  flex: 1;
  padding-left: 0.8rem;
  border-left: 4px solid #e4e6e9;
}

.election-map .tooltip .results .status {
  padding: 0 .5rem;
  margin-right: .3rem;
  font-family: Fette, "Arial Narrow", sans-serif;
  text-transform: uppercase;
  font-weight: normal;
  font-size: 1.5rem;
  color: #fff;
  background-color: #a2a9ae;
}

.election-map .tooltip .results .status.elu {
  background-color: #daa520;
}

.election-map .tooltip .results .nuance {
  display: block;
  color: #747b83;
  font-size: 1rem;
  text-transform: uppercase;
  font-weight: normal;
}

.election-map .tooltip .results .score {
  width: 10rem;
  text-align: right;
  font-size: 1.8rem;
  margin-left: .5rem;
}

.election-map .tooltip .btn-close {
  float: right;
}

.election-map .legend {
  font-size: 11px;
  margin: 12px 0;
}

.election-map .legend li {
  display: inline-block;
  text-align: left;
  margin: 0 0 4px;
  width: 49%;
  border-left: 6px solid transparent;
  padding-left: 6px;
}

.election-map .legend li:before {
  display: none;
}

@media screen and (min-width: 768px) {
  .election-map .legend {
    font-size: 12px;
  }

  .election-map .legend li {
      width: 32%;
  }
}

@media screen and (max-width: 640px) {
  .election-map .tooltip {
    position: fixed !important;
    top: auto !important;
    right: 0 !important;
    left: 0 !important;
    bottom: 0 !important;
  }
}

@media (min-width: 768px) {
  .election-map {
    position: relative;
  }

  .election-map .tooltip {
    border: 1px solid #b9c0c5;
    box-shadow: 1px 1px 1px 0px #b9c0c5;
    position: absolute;
  }

  .election-map .tooltip .results {
    min-width: 230px;
  }

  .election-map .tooltip .btn-close {
    display: none;
  }
}



